import VendasService from '@/services/api/vendas/VendasService';
import { Bar } from 'vue-chartjs';
import { Chart as ChartJS, Title, Tooltip, Legend, BarElement, CategoryScale, LinearScale } from 'chart.js';
ChartJS.register(Title, Tooltip, Legend, BarElement, CategoryScale, LinearScale);
export default {
  props: ['modelValue', 'cliente'],
  emits: ['update:modelValue'],
  name: 'BarChart',
  components: {
    Bar
  },
  data() {
    return {
      selectedPeople: this.modelValue,
      loading: false,
      dataLoaded: false,
      chartData: {
        labels: [],
        datasets: [{
          label: 'Total quantidade de vendas',
          backgroundColor: '#87CEFA',
          data: []
        }]
      },
      chartOptions: {
        responsive: true,
        indexAxis: 'y'
      }
    };
  },
  watch: {
    modelValue(newValue) {
      this.selectedPeople = newValue;
    },
    cliente: {
      immediate: true,
      // Isto garante que a função é chamada na inicialização
      handler(newValue) {
        this.getAll();
      }
    }
  },
  methods: {
    async getAll() {
      try {
        const params = {
          all: '0',
          agrupar_cliente: '1',
          cliente: this.cliente
        }; // Utiliza this.cliente diretamente
        this.loading = true;
        const response = await VendasService.getAll(params);
        this.chartData.datasets[0].data = response.map(item => parseFloat(item.total_preco.replace(',', '.')));
        this.chartData.labels = response.map(item => item.pessoa.nome);
        this.dataLoaded = true;
      } catch (error) {
        console.error('Erro ao obter vendas:', error);
      } finally {
        this.loading = false;
      }
    }
  }
};
