import grupoPessoaService from '@/services/api/grupoProduto/grupoProdutoService';
export default {
  data() {
    return {
      selectedGroup: '',
      groups: [],
      loading: false
    };
  },
  methods: {
    async getAll() {
      try {
        this.loading = true;
        const response = await grupoPessoaService.getAll();
        this.groups = response;
      } catch (error) {
        console.error('Erro ao obter grupos:', error);
      } finally {
        this.loading = false;
      }
    },
    handleFilterChange() {
      this.$emit('group-selected', this.selectedGroup);
    }
  },
  mounted() {
    this.getAll();
  }
};
