import axios from 'axios';

const API_URL = 'http://localhost:8000/api';

const PessoasService = {
  async getPessoa() {
    try {
      const response = await axios.get(`${API_URL}/pessoa`);
      return response.data.pessoa;
    } catch (error) {
      console.error('Erro ao obter registro de pessoas:', error);
      throw new Error('Erro ao obter registro de pessoas da API.');
    }
  }
};

export default PessoasService;
