import axios from 'axios';

const API_URL = 'http://localhost:8000/api';
const PessoasService = {
  async getAll() {
    try {
      const token = localStorage.getItem('userToken');

      const response = await axios.get(`${API_URL}/pessoa`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      console.log(response.data.pessoa);
      return response.data;
    } catch (error) {
      console.error('Erro ao obter produtos:', error);
      throw new Error('Erro ao obter produtos da API.');
    }
  },

  async cadastrar(data) {
    try {
        const token = localStorage.getItem('userToken');

        const response = await axios.post(`${API_URL}/pessoa`, data,{
            headers: {
              Authorization: `Bearer ${token}`
            }
          });

      if (response.status === 201) {
        return { success: true, message: 'Pessoa cadastrada com sucesso' };
      }
    } catch (error) {
      console.error('Erro ao cadastrar a pessoa:', error);
      return { success: false, message: 'Erro ao cadastrar a pessoa' };
    }
  },

  async atualizar(id, newData) {
    const token = localStorage.getItem('userToken');
    try {
      const response = await axios.put(`${this.baseURL}/pessoa/${id}`, newData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      return response.data;
    } catch (error) {
      throw new Error('Falha ao atualizar pessoa');
    }
  },

  // Método para remover pessoa
  async remover(id) {
    const token = localStorage.getItem('userToken');

    try {
      const response = await axios.delete(`${this.baseURL}/pessoa/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      return response.data;
    } catch (error) {
      throw new Error('Falha ao remover pessoa');
    }
  }

};

export default PessoasService;

