// ProdutoService.js
import axios from 'axios';

const API_URL = 'http://localhost:8000/api'; // Coloque a URL base da sua API aqui

const ProdutoService = {
  async obterProdutos() {
    try {
      const token = localStorage.getItem('userToken'); // Obtém o token de localStorage

      const response = await axios.get(`${API_URL}/pessoas`, {
        headers: {
          Authorization: `Bearer ${token}` // Adiciona o token ao cabeçalho de autorização
        }
      });

      return response.data.produto;
    } catch (error) {
      console.error('Erro ao obter produtos:', error);
      throw new Error('Erro ao obter produtos da API.');
    }
  }
};

export default ProdutoService;
