import { createRouter, createWebHashHistory } from 'vue-router';
import '@fortawesome/fontawesome-free/css/all.css';
import '@fortawesome/fontawesome-free/js/all.js';

/* const LoginComponent = () => import('../pages/Login/LoginComponent');
const HomeComponent = () => import('../pages/Home/HomeComponent');
const PessoaComponent = () => import('../pages/Pessoa/PessoaComponent');
const ProdutoComponent = () => import('../pages/Produto/ProdutoComponent');
const VendaComponent = () => import('../pages/Venda/VendaComponent');
const CompraComponent = () => import('../pages/Compra/CompraComponent');
 */
import LoginComponent from '../pages/Login/LoginComponent.vue';
import HomeComponent from '../pages/Home/HomeComponent.vue';
import PessoaComponent from '../pages/Pessoa/PessoaComponent.vue';
import DetailPessoaComponent from '../pages/Pessoa/DetailComponent.vue';
import ListPessoaComponent from '../pages/Pessoa/ListComponent.vue';
import ProdutoComponent from '../pages/Produto/ProdutoComponent.vue';
import VendaComponent from '../pages/Venda/VendaComponent.vue';
import CompraComponent from '../pages/Compra/CompraComponent.vue';


const routes = [
  { path: '/', name:'login', component: LoginComponent },
  { path: '/home', name:'home', component: HomeComponent },
  { path: '/pessoa', name:'pessoa', component: PessoaComponent },
  { path: '/pessoa', name:'pessoaLista', component: ListPessoaComponent },
  { path: '/pessoa/detalhes/:id', name:'pessoaDetalhe', component: DetailPessoaComponent },
  { path: '/produto', name:'produto', component: ProdutoComponent },
  { path: '/venda', name:'venda', component: VendaComponent },
  { path: '/compra', name:'compra', component: CompraComponent },
];

const router = createRouter({
  history: createWebHashHistory(),
  routes,
});

export default router;

export const drawerOptions = [
  { icon: 'fas fa-home', path: '/home', label: 'Home' },
  { icon: 'fas fa-user', path: '/pessoa', label: 'Pessoa' },
  { icon: 'fas fa-box', path: '/produto', label: 'Produto' },
  { icon: 'fas fa-money-bill-wave', path: '/venda', label: 'Venda' },
  { icon: 'fas fa-shopping-cart', path: '/compra', label: 'Compra' },
];
