import ProdutosService from '@/services/api/produtos/ProdutosService';
export default {
  props: ['modelValue', 'initialGroup'],
  emits: ['update:modelValue'],
  data() {
    return {
      selectedProduct: this.modelValue,
      produtos: [],
      loading: false
    };
  },
  watch: {
    modelValue(newValue) {
      this.selectedProduct = newValue;
    },
    selectedProduct(newValue) {
      this.$emit('update:modelValue', newValue);
    }
  },
  methods: {
    async getAll() {
      try {
        const params = {
          all: '0'
        };
        this.loading = true;
        const response = await ProdutosService.getAll(params);
        this.produtos = response;
      } catch (error) {
        console.error('Erro ao obter produtos:', error);
      } finally {
        this.loading = false;
      }
    },
    emitProduct() {
      this.$emit('update:modelValue', this.selectedProduct);
      this.$emit('product-selected', this.selectedProduct);
    }
  },
  mounted() {
    if (!this.initialProduct) {
      this.getAll();
    }
  }
};
